import { Component } from '@angular/core';

@Component({
  selector: 'app-top-nav',
  templateUrl: './top-nav.component.html',
  styleUrls: ['./top-nav.component.scss'],
})
export class TopNavComponent {
  navLinks = [
    {
      name: 'Home',
      link: '/home',
    },
    {
      name: 'Products',
      link: '/products',
      sublinks: [
        {
          name: 'Category 1',
          link: '/category1',
        },
        {
          name: 'Category 2',
          link: '/category2',
        },
        {
          name: 'Category 3',
          link: '/category3',
        },
      ],
    },
    {
      name: 'About',
      link: '/about',
    },
    {
      name: 'Contact',
      link: '/contact',
    },
  ];

  constructor() {}
}

interface NavLink {
  name: string;
  link: string;
}
