import { Component } from '@angular/core';

@Component({
  selector: 'app-top-nav',
  templateUrl: './top-nav.component.html',
  styleUrls: ['./top-nav.component.scss']
})
export class TopNavComponent {
  navLinks: NavLink[] = [
    { name: 'Home', link: '#' },
    { name: 'Work', link: '#', subLinks: [
        { name: 'Project A', link: '#' },
        { name: 'Project B', link: '#' },
        { name: 'Project C', link: '#' },
      ] 
    },
    { name: 'Services', link: '#' },
    { name: 'About Us', link: '#' },
    { name: 'Contact Us', link: '#' }
  ]; // Define an array of navigation links

  constructor() { }
}

interface NavLink {
  name: string;
  link: string;
  subLinks?: NavLink[];
}
